from labs.grading import Default as GuidedExercise
from labs.common.userinterface import Console
from ad482.common import prechecks, steps


class CollaborationStateful(GuidedExercise):

    __LAB__ = "collaboration-stateful"

    """
    Stateful Collaboration Guided Exercise
    """

    topics = ["vehicles", "vehicle-movements"]

    def start(self):
        prechecks.verify_config()
        items = [
            steps.pull_apps_repo(),
            steps.copy_lab_files(f"{self.__LAB__}/apps", self.__LAB__),
            steps.copy_env_file(f"{self.__LAB__}/vehicles/.env",
                                ".env-streams"),
            steps.check_ocp_api(),
            steps.check_ocp_credentials(),
            steps.move_to_kafka_cluster_namespace(),
            steps.check_kafka_connection(),
            steps.create_topics(self.topics)
        ]

        console = Console(items)
        console.run_items(action="Starting")

    def finish(self):
        items = [
            steps.check_ocp_api(),
            steps.check_ocp_credentials(),
            steps.move_to_kafka_cluster_namespace(False),
            steps.delete_topics(self.topics),
            steps.delete_state_store_topics([
                "vehicles-vehicle-metrics-store-changelog"
            ])
        ]
        Console(items).run_items(action="Finishing")
