from typing import NamedTuple

from ad482.common.classroom import workspace


class KafkaConfig(NamedTuple):
    bootstrap_server: str
    bootstrap_port: int
    cert_path: str
    cluster_namespace: str

    @classmethod
    def from_workspace(cls):
        return cls(
            workspace.config.kafka_bootstrap_server,
            workspace.config.kafka_bootstrap_port,
            workspace.config.kafka_cert_path,
            workspace.config.kafka_cluster_namespace)
