import json
from typing import Any


def int_deserializer(x: bytes):
    return int.from_bytes(x, "big")


def int_serializer(x: int):
    return x.to_bytes(4, "big")


def long_deserializer(x: bytes):
    return int_deserializer(x)


def long_serializer(x: int):
    print(type(x))
    return x.to_bytes(8, "big")


def json_serializer(x: Any):
    return json.dumps(x).encode("utf-8")


def json_deserializer(x: bytes):
    return json.loads(x.decode("utf-8"))


def string_serializer(x):
    return x.encode("utf-8")


def string_deserializer(x: bytes):
    return x.decode("utf-8")
