from ad482.common import prechecks
from labs.grading import Default as GuidedExercise
from labs.common.userinterface import Console
from ad482.common import steps


class KafkaTopics(GuidedExercise):
    __LAB__ = "kafka-topics"

    """
    Configures lab environment

    Configures the workspace directory.
    Saves the config in the $HOME/.grading/ad482-workspace.json file
    """

    def start(self):
        prechecks.verify_config()
        items = [
            steps.pull_apps_repo(),
            steps.copy_lab_files(f"{self.__LAB__}/apps",
                                 self.__LAB__),
            steps.check_ocp_api(),
            steps.check_ocp_credentials(),
            steps.move_to_kafka_cluster_namespace(),
            steps.check_kafka_connection()
        ]
        Console(items).run_items(action="Starting")

    def finish(self):
        items = [
            steps.check_ocp_api(),
            steps.check_ocp_credentials(),
            steps.move_to_kafka_cluster_namespace(False),
            steps.delete_topics(
                ['call-detail-records', 'one-partition-topic']
            )
        ]
        Console(items).run_items(action="Finishing")
