from pathlib import Path

from labs.grading import Default as GuidedExercise
from labs.common.userinterface import Console
from ad482.common import prechecks, steps


class TroubleShootingDuplication(GuidedExercise):

    __LAB__ = "troubleshooting-duplication"

    """
    Guided Exercise: Handling Duplicated Events
    """

    topics = [
        "movement-reported-events",
        "speed-values"
    ]

    transaction_topics = [
        'transaction-state'
    ]

    def start(self):
        prechecks.verify_config()
        items = [
            steps.pull_apps_repo(),
            steps.copy_lab_files(Path(self.__LAB__, "apps"), self.__LAB__),
            steps.check_ocp_api(),
            steps.check_ocp_credentials(),
            steps.move_to_kafka_cluster_namespace(),
            steps.check_kafka_connection(),
            steps.create_topics(self.topics)
        ]
        Console(items).run_items(action="Starting")

    def finish(self):
        items = [
            steps.check_ocp_api(),
            steps.check_ocp_credentials(),
            steps.move_to_kafka_cluster_namespace(False),
            steps.delete_topics(self.topics),
            steps.delete_state_store_topics(self.transaction_topics),
        ]
        Console(items).run_items(action="Finishing")
